    READS SQL DATA
BEGIN
	DECLARE mVariable1 INT;
    DECLARE mVariable2 INT;
    
    -- Get Anime ID
    SELECT anime_id INTO mVariable1
    FROM myanimelist 
    WHERE title = p_title;
    
