        SELECT 'Update';
	ELSE
		-- Insert
        INSERT INTO entries(user_id, anime_id, my_watched_episodes, my_score, my_status)
        VALUE(p_identification, mVariable1, p_watched, p_score, p_status);
        
        -- Return
        SELECT 'Insert';
	END IF;
		
